import React, { useState } from 'react';
import './App.css';
import ChessGame from './components/ChessGame.js';

function App() {
  const [playerColor, setPlayerColor] = useState('white');
  const [difficulty, setDifficulty] = useState('medium');
  const [showSettings, setShowSettings] = useState(false);
  
  // Map difficulty levels to search depth
  const difficultyMap = {
    easy: 5,
    medium: 12,
    hard: 18,
    expert: 22
  };

  const handleNewGame = () => {
    // The ChessGame component will handle the reset
    // We just need to toggle a state to trigger it
    setShowSettings(false);
  };

  return (
    <div className="App">
      <header className="App-header">
        <h1>Chess with Stockfish</h1>
      </header>
      <main>
        <div className="controls-panel">
          <div className="button-group">
            <button 
              className="btn btn-primary"
              onClick={() => setPlayerColor(playerColor === 'white' ? 'black' : 'white')}
            >
              Play as {playerColor === 'white' ? 'Black' : 'White'}
            </button>
            <button 
              className="btn btn-success" 
              onClick={handleNewGame}
            >
              New Game
            </button>
          </div>
          
          <div className="difficulty-selector">
            <label htmlFor="difficulty">AI Difficulty:</label>
            <select 
              id="difficulty" 
              value={difficulty} 
              onChange={(e) => setDifficulty(e.target.value)}
            >
              <option value="easy">Easy</option>
              <option value="medium">Medium</option>
              <option value="hard">Hard</option>
              <option value="expert">Expert</option>
            </select>
          </div>
        </div>
        
        <ChessGame 
          playerColor={playerColor} 
          searchDepth={difficultyMap[difficulty]}
          key={`${playerColor}-${difficulty}`} // Force re-render on color/difficulty change
        />
      </main>
      <footer>
        <p>Powered by Stockfish Chess Engine | <a href="https://github.com/official-stockfish/Stockfish" target="_blank" rel="noopener noreferrer">GitHub</a></p>
      </footer>
    </div>
  );
}

export default App;
