import React, { useState } from 'react';
import './App.css';
import ChessGame from './components/ChessGame.js';

function App() {
  const [playerColor, setPlayerColor] = useState('white');

  return (
    <div className="App">
      <header className="App-header">
        <h1>Chess with Stockfish</h1>
      </header>
      <main>
        <div className="controls">
          <button
            onClick={() => setPlayerColor(playerColor === 'white' ? 'black' : 'white')}
            className="switch-color-btn"
          >
            Switch to {playerColor === 'white' ? 'Black' : 'White'}
          </button>
        </div>
        <ChessGame playerColor={playerColor} />
      </main>
    </div>
  );
}

export default App;
